OrderModel.schema.path('_id').validate(async function(value: number): Promise<boolean>{
   
    while(true)
    {
        this._id = Math.floor(Math.random() * 1000000) + 1;
        let order: Order = await OrderModel.findById(this._id).exec();

        if(!order)
        {
            break;
        }

    }

