import * as express from "express";
import * as AppUser from "../../common/constants/user.constants";
import { InvoiceMap } from "../../models/maps/invoice.map";
import { ApplicationUser } from "../../models/user.model";
import { InvoiceService } from "../../services/invoice.service";
import { PaginatedOrders } from "../../models/order-pagination.model";

@Tags("Invoices")
@Security(AppUser.JWT_SECURITY, [AppUser.ADMIN_ROLE])
@Route("invoices")
export class InvoiceController extends Controller 
{
    /**
     * <b>Purchase an order your admin has created and use it for up to 10 times</b> 
     */
    @Post('purchase')
    @Security(AppUser.JWT_SECURITY, [AppUser.USER_ROLE])
    public async PurchaseOrder(@Request() request: express.Request, @Body() invoicemap: InvoiceMap): Promise<void> 
    {
        const applicationUser = (request as any).applicationUser as ApplicationUser;

        await InvoiceService.getInstance().PurchaseOrder(applicationUser, invoicemap);
    }

    /**
     * <b>See all the commands you have purchased in the past</b>
     */
    @Get('start/{startIndex}/end/{endIndex}')
    @Security(AppUser.JWT_SECURITY, [AppUser.USER_ROLE])
    public async GetInvoices(@Request() request: express.Request, startIndex: number = 0, endIndex = 49): Promise<PaginatedOrders> 
    {
        const applicationUser = (request as any).applicationUser as ApplicationUser;

        return await InvoiceService.getInstance().GetInvoices(applicationUser, startIndex, endIndex);
    }

    /**
     * <b>Request a list of invoices of commands my users are buying</b>
     */
    @Get('users/start/{startIndex}/end/{endIndex}')
    @Security(AppUser.JWT_SECURITY, [AppUser.USER_ROLE])
    public async GetMyUsersInvoices(@Request() request: express.Request, startIndex: number = 0, endIndex = 49): Promise<PaginatedOrders> 
    {
        const applicationUser = (request as any).applicationUser as ApplicationUser;

        return await InvoiceService.getInstance().GetMyUserInvoices(applicationUser, startIndex, endIndex);
    }

    /**
     * <b>Get the data of a specific invoice</b>
     */
    @Get('{invoiceId}')
    @Security(AppUser.JWT_SECURITY, [AppUser.USER_ROLE])
    public async GetInvoice(@Request() request: express.Request, invoiceId: number): Promise<InvoiceMap> 
    {
        const applicationUser = (request as any).applicationUser as ApplicationUser;

        return await InvoiceService.getInstance().GetInvoice(applicationUser, invoiceId);
    }
}

import { application } from "express";
import { PaginatedInvoices } from "../models/invoice-pagination.model";
import { Invoice, InvoiceModel } from "../models/Invoice.model";
import { InvoiceMap } from "../models/maps/invoice.map";
import { UserMap } from "../models/maps/user.map";
import { Order, OrderModel } from "../models/order.model";
import { ApplicationUser, UserModel } from "../models/user.model";
import { AuthenticationService } from "./authentication.service";
import { MapperService } from "./mapper.service";

export class InvoiceService 
{
    private static instance: InvoiceService;

    private constructor() {}

    public static getInstance(): InvoiceService 
    {
        if (!InvoiceService.instance) 
        {
            InvoiceService.instance = new InvoiceService();
        }

        return InvoiceService.instance;
    }

    public async PurchaseOrder(user: ApplicationUser, invoicemap: InvoiceMap): Promise<void> 
    {
        invoicemap.userId = user._id;

        let invoice = MapperService.getInstance().invoiceMapToInvoice(invoicemap);
        await InvoiceModel.create(invoice);

        let order: Order = await OrderModel.findOne({_id: invoicemap.orderId, adminId: user.adminId });
        invoicemap.totalPrice = order.price;
        await UserModel.update({_id: user._id}, {$inc: { funds: (invoicemap.totalPrice * -1) }})
    }

    public async GetInvoices(applicationUser: ApplicationUser, startIndex: number, endIndex: number): Promise<PaginatedInvoices>
    {
        let invoices: Invoice[] = new Array();
        let garbage = await InvoiceModel.find({ userId: applicationUser._id});
        garbage.forEach(item => {
            invoices.push(new InvoiceModel(item));
        });

        let paginatedInvoices = new PaginatedInvoices();
        paginatedInvoices.total = invoices.length;
        paginatedInvoices.invoices = MapperService.getInstance().invoiceArrayToInvoiceMapArray(invoices.slice(startIndex, endIndex));

        return paginatedInvoices;
    }
    
    public async GetInvoice(applicationUser: ApplicationUser, invoiceId: number): Promise<InvoiceMap>
    {
        let invoice = new InvoiceModel(await InvoiceModel.find({ userId: applicationUser._id, _id: invoiceId}));

        return MapperService.getInstance().invoiceToInvoiceMap(invoice);
    }

    public async GetMyUserInvoices(admin: ApplicationUser, startIndex: number = 0, endIndex: number = 49): Promise<PaginatedInvoices>
    {
        let users: UserMap[] = await AuthenticationService.getInstance().GetUsersByAdmin(admin);

        let invoices: Invoice[] = new Array();
        let garbage = await InvoiceModel.find({ $in: { userId: users.map(s => s._id)} });
        garbage.forEach(item => {
            invoices.push(new InvoiceModel(item));
        });
        
        let paginatedInvoices = new PaginatedInvoices();
        paginatedInvoices.total = invoices.length;
        paginatedInvoices.invoices = MapperService.getInstance().invoiceArrayToInvoiceMapArray(invoices.slice(startIndex, endIndex));

        return paginatedInvoices
    }
    
}

